import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
// import OAuth from '../components/OAuth'; // Uncomment if you have OAuth

const SignIn = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [localError, setLocalError] = useState(null);
  const navigate = useNavigate();
  const { signin, loading, error } = useAuth();

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLocalError(null);

    const success = await signin(formData.email, formData.password);
    if (success) {
      navigate('/');
    } else {
      setLocalError(error || 'Invalid email or password');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-8 bg-white rounded-lg shadow-md">
      <h1 className="text-4xl font-bold text-center text-slate-800 mb-6">
        Welcome Back
      </h1>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input
          type="email"
          id="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
          required
        />
        <input
          type="password"
          id="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
          required
        />
        <button
          type="submit"
          className="bg-slate-700 text-white p-3 rounded-md font-medium hover:bg-slate-700 transition disabled:opacity-60"
          disabled={loading}
        >
          {loading ? 'Signing in...' : 'Sign In'}
        </button>
      </form>

      {/* <OAuth /> Uncomment if you have OAuth */}

      <div className="text-center mt-4 text-sm text-gray-600">
        Donâ€™t have an account?{' '}
        <Link to="/sign-up" className="text-blue-600 hover:underline">
          Sign Up
        </Link>
      </div>

      {(localError || error) && (
        <p className="text-red-600 text-center mt-4 font-medium">
          {localError || error}
        </p>
      )}
    </div>
  );
};

export default SignIn;
