import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/RemedyDetail.css";

export default function RemedyDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [remedy, setRemedy] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("/remedy.json")
      .then((res) => res.json())
      .then((data) => {
        const found = data.find((r) => String(r.id) === id);
        setRemedy(found);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error loading remedy data:", err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="remedy-detail-loading">Loading...</p>;
  if (!remedy) return <p className="remedy-detail-error">Remedy not found.</p>;

  return (
    <section
      className="remedy-detail-container"
      aria-label={`Details about ${remedy.name}`}
    >
      <button
        onClick={() => navigate(-1)}
        className="remedy-detail-back-btn"
      >
        &larr; Back
      </button>

      <h2 className="remedy-detail-title">{remedy.name}</h2>

      <img
        src={remedy.imageUrl}
        alt={`Image of ${remedy.name}`}
        className="remedy-detail-image"
      />

      <div className="remedy-detail-content">
        <Section title="Description" content={remedy.description} />
        <Section title="Ingredients" list={remedy.ingredients} isList />
        <Section title="Preparation Method" list={remedy.preparationMethod} isList ordered />
        <Section title="Dosage" content={remedy.dosage} />
        <Section title="Benefits" content={remedy.benefits} />
        <Section title="Precautions" content={remedy.precautions} />
      </div>
    </section>
  );
}

// Section Component
function Section({ title, content, list, isList, ordered }) {
  return (
    <section className="remedy-detail-section">
      <h3 className="remedy-detail-section-title">{title}</h3>
      {isList && Array.isArray(list) ? (
        ordered ? (
          <ol className="remedy-detail-list">{list.map((item, i) => <li key={i}>{item}</li>)}</ol>
        ) : (
          <ul className="remedy-detail-list">{list.map((item, i) => <li key={i}>{item}</li>)}</ul>
        )
      ) : (
        <p className="remedy-detail-section-content">{content || "Not available"}</p>
      )}
    </section>
  );
}
