import {createSlice} from '@reduxjs/toolkit';

const initialState={
    currentUser:null,
    error:null,
    loading:null,
}

const userSlice =createSlice({
    name: 'user',
    initialState,
    reducer:{
        signInStart:(state)=>{
            state.loading=true;
        },
        signInSuccess:(state,action)=>{
            state.loading=false;
            state.error=action.playload;
            state.error=null;
        },
        signInFailure:(state,action)=>{
            state.loading=false;
            state.loading=action.playload;
        },
    },
})
