import { useState } from 'react';
import { FaSearch, FaTimes } from 'react-icons/fa';
import { Link } from 'react-router-dom';

import '../styles/Header.css';

const Header = ({ currentUser, onSearch }) => {
  const [searchInput, setSearchInput] = useState('');

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchInput(query);
    onSearch(query);
  };

  const clearSearch = () => {
    setSearchInput('');
    onSearch('');
  };

  return (
    <header className="header">
      <div className="header-container">
        <Link to="/" className="logo" aria-label="Homepage">
          <h1 className="logo-text">
            <span className="logo-light">Panch</span>
            <span className="logo-dark">karma</span>
          </h1>
        </Link>

        <form className="search-form" onSubmit={e => e.preventDefault()}>
          <input
            type="text"
            placeholder="Search..."
            className="search-input"
            value={searchInput}
            onChange={handleSearchChange}
            aria-label="Search"
          />
          {searchInput ? (
            <FaTimes className="search-clear-icon" onClick={clearSearch} title="Clear" />
          ) : (
            <FaSearch className="search-icon" />
          )}
        </form>

        <ul className="nav-links">
          <li><Link to="/" className="nav-link">Home</Link></li>
          <li><Link to="/about" className="nav-link">About</Link></li>
          <li>
            {currentUser ? (
              <div className="auth-logged-in">
                <Link to="/profile" className="profile-link" aria-label="Profile">
                  <div className="header-avatar">
                    <img
                      src={currentUser.avatar || '/assets/avatar.png'}
                      alt="avatar"
                    />
                  </div>
                </Link>
              </div>
            ) : (
              <Link to="/sign-in" className="nav-link">Sign In</Link>
            )}
          </li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
