import React from 'react';
import { FaSearch } from "react-icons/fa";
import { Link } from "react-router-dom";

const Header = ({ currentUser }) => {
  // Helper to build proper image URL
  const getAvatarUrl = (avatar) => {
    if (!avatar) return null;
    return avatar.startsWith('/') ? avatar : `/${avatar}`;
  };

  return (
    <header className="bg-slate-200 shadow-md">
      <div className='flex justify-between items-center max-w-6xl mx-auto p-3'>
        <Link to='/'>
          <h1 className="font-bold text-sm sm:text-xl flex flex-wrap">
            <span className="text-slate-500">Dream</span>
            <span className="text-slate-700">Estate</span>
          </h1>
        </Link>
        <form className='bg-slate-100 p-3 rounded-lg flex justify-items-center'>
          <input
            type="text"
            placeholder="Search..."
            className="bg-transparent focus:outline-none w-24 sm:w-64"
          />
          <FaSearch className='text-slate-600' />
        </form>
        <ul className='flex gap-4 items-center'>
          <li>
            <Link to='/' className='hidden sm:inline text-slate-700 hover:underline'>
              Home
            </Link>
          </li>
          <li>
            <Link to='/About' className='hidden sm:inline text-slate-700 hover:underline'>
              About
            </Link>
          </li>
          <li>
            <Link to='/profile'>
              {currentUser ? (
                <img
                  src={getAvatarUrl(`assets/${currentUser.avatar}`)}
                  alt="avatar"
                  className="w-8 h-8 rounded-full object-cover"
                />
              ) : (
                <span className='text-slate-700 hover:underline cursor-pointer'>Sign In</span>
              )}
            </Link>
          </li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
