import React, { useState, useEffect, useRef } from "react";

const Profile = ({ currentUser, setCurrentUser }) => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    avatar: "",
  });

  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const fileRef = useRef(null);
  const [file, setFile] = useState(undefined);

  const [filePerc, setFilePerc] = useState(0);
  const [fileUploadError, setFileUploadError] = useState(false);

  useEffect(() => {
    if (currentUser) {
      setFormData({
        username: currentUser.username || "",
        email: currentUser.email || "",
        password: "",
        avatar: currentUser.avatar || "",
      });
    }
  }, [currentUser]);

  useEffect(() => {
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prev) => ({ ...prev, avatar: reader.result }));
        setFileUploadError(false);
        setFilePerc(100); // Simulated upload
      };
      reader.onerror = () => {
        setFileUploadError(true);
        setFilePerc(0);
      };
      reader.readAsDataURL(file);
    }
  }, [file]);

  if (!currentUser) {
    return <p className="text-center text-gray-600 mt-10">No user data available</p>;
  }

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.id]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setMessage(null);
    setError(null);

    if (!currentUser || !currentUser.id) {
      setError("User ID is missing. Please sign in again.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:5000/user/update/${currentUser.id}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify(formData),
        }
      );

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || "Failed to update profile");
      } else {
        setMessage("Profile updated successfully!");
        setFormData((prev) => ({ ...prev, password: "" }));

        if (typeof setCurrentUser === "function") {
          setCurrentUser(data.user);
        }
      }
    } catch (err) {
      console.error("Error updating profile:", err);
      setError("An unexpected error occurred.");
    }
  };

  const handleDeleteAccount = async () => {
    setMessage(null);
    setError(null);

    if (!currentUser || !currentUser.id) {
      setError("User ID is missing. Please sign in again.");
      return;
    }

    if (!window.confirm("Are you sure you want to delete your account? This action cannot be undone.")) {
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:5000/user/delete/${currentUser.id}`,
        {
          method: "DELETE",
          credentials: "include",
        }
      );

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || "Failed to delete account");
      } else {
        setMessage("Account deleted successfully.");
        if (typeof setCurrentUser === "function") {
          setCurrentUser(null);
        }
      }
    } catch (err) {
      console.error("Error deleting account:", err);
      setError("An unexpected error occurred.");
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-white rounded-lg shadow-md mt-10">
      <h1 className="text-4xl font-bold text-center text-slate-800 mb-6">Your Profile</h1>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input
          type="file"
          accept="image/*"
          ref={fileRef}
          onChange={(e) => setFile(e.target.files[0])}
          hidden
        />

        <div className="self-center">
          <img
            onClick={() => fileRef.current.click()}
            src={formData.avatar || "/assets/avatar.png"}
            alt="profile"
            className="rounded-full h-28 w-28 object-cover cursor-pointer shadow-md border-2 border-slate-300 hover:scale-105 transition duration-200"
          />
          <p className="text-sm text-center mt-2">
            {fileUploadError ? (
              <span className="text-red-600">Image upload failed (max 5MB)</span>
            ) : filePerc > 0 && filePerc < 100 ? (
              <span className="text-slate-600">Uploading {filePerc}%...</span>
            ) : filePerc === 100 ? (
              <span className="text-green-600">Image uploaded!</span>
            ) : null}
          </p>
        </div>

        <input
          type="text"
          id="username"
          value={formData.username}
          onChange={handleChange}
          placeholder="Username"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
          required
        />

        <input
          type="email"
          id="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
          required
        />

        <input
          type="password"
          id="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="New Password (optional)"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
        />

        <button
          type="submit"
          className="bg-slate-700 text-white py-3 rounded-md text-lg font-medium hover:opacity-95 transition duration-200"
        >
          Update Profile
        </button>
      </form>
        <div className="flex justify-between mt-5">
          <span className="text-red-700 cursor-pointer" onClick={handleDeleteAccount}>Delete account</span>
          <span className="text-red-700 cursor-pointer">Sign Out</span>
        </div>
      {message && <p className="text-green-600 text-center mt-4">{message}</p>}
      {error && <p className="text-red-600 text-center mt-4">{error}</p>}
    </div>
  );
};

export default Profile;
