import React from 'react';
import { FaSearch } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';

const Header = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();

  const handleSignout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setCurrentUser(null);
    navigate('/');
  };

  return (
    <header className="bg-slate-200 shadow-md">
      <div className="flex justify-between items-center max-w-6xl mx-auto p-3">
        {/* Logo */}
        <Link to="/">
          <h1 className="font-bold text-sm sm:text-xl flex flex-wrap cursor-pointer">
            <span className="text-slate-500">Panch</span>
            <span className="text-slate-700">karma</span>
          </h1>
        </Link>

        {/* Search Bar */}
        <form className="bg-slate-100 p-3 rounded-lg flex items-center">
          <input
            type="text"
            placeholder="Search..."
            className="bg-transparent focus:outline-none w-24 sm:w-64"
          />
          <FaSearch className="text-slate-600" />
        </form>

        {/* Navigation */}
        <ul className="flex gap-4 items-center">
          <li>
            <Link to="/" className="hidden sm:inline text-slate-700 hover:underline">
              Home
            </Link>
          </li>
          <li>
            <Link to="/about" className="hidden sm:inline text-slate-700 hover:underline">
              About
            </Link>
          </li>
          <li>
            {currentUser ? (
              <div className="flex items-center gap-4">
                <Link to="/profile" className="flex items-center gap-2">
                  <img
                    src={currentUser.avatar || '/assets/avatar.png'}
                    alt="avatar"
                    className="w-8 h-8 rounded-full object-cover"
                  />
                  
                </Link>
                <button
                  onClick={handleSignout}
                  className="text-red-600 hover:underline text-sm"
                >
                  Sign Out
                </button>
              </div>
            ) : (
              <Link to="/sign-in" className="text-slate-700 hover:underline cursor-pointer">
                Sign In
              </Link>
            )}
          </li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
