import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import RemedyCard from "./RemedyCard";
import { useAuth } from "../context/AuthContext";

export function Hero() {
  return (
    <section
      style={{ position: "relative", textAlign: "center", marginBottom: 40 }}
      aria-label="Homepage Hero Section"
    >
      <img
        src="https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/3626d480-e17b-42d3-851b-d23b1d889bc3.png"
        alt="Natural Ayurvedic herbs and remedies display"
        style={{
          width: "100%",
          maxHeight: 400,
          objectFit: "cover",
          borderRadius: 12,
          filter: "brightness(0.75)",
        }}
      />
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "white",
          maxWidth: "90%",
          textShadow: "0 0 12px rgba(0,0,0,0.7)",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", fontWeight: 700, marginBottom: 12 }}>
          Welcome to Ayurveda Healing
        </h1>
        <p style={{ fontSize: "1.25rem", fontWeight: 300 }}>
          Discover natural and home-based Ayurvedic remedies for holistic health
          and wellness.
        </p>
      </div>
    </section>
  );
}

function RemediesGrid({ remedies, onCardClick }) {
  if (!remedies.length) {
    return <p style={{ padding: "1rem" }}>No remedies found.</p>;
  }
  return (
    <section
      aria-label="List of Ayurvedic remedies"
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
        gap: 20,
        marginBottom: 40,
        padding: "0 1rem",
      }}
    >
      {remedies.map((remedy) => (
        <RemedyCard key={remedy.id} remedy={remedy} onSelect={() => onCardClick(remedy)} />
      ))}
    </section>
  );
}

export default function Home({ searchQuery = "" }) {
  const { user } = useAuth();
  const [remediesData, setRemediesData] = useState([]);
  const [filteredRemedies, setFilteredRemedies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("/remedy.json")
      .then((response) => response.json())
      .then((data) => {
        setRemediesData(data);
        setFilteredRemedies(data);
      });
  }, []);

  useEffect(() => {
    let result = remediesData;

    if (searchQuery.trim()) {
      result = result.filter((rem) =>
        rem.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFilteredRemedies(result);
  }, [searchQuery, remediesData]);

  const handleRemedyClick = (remedy) => {
    if (user) {
      navigate(`/remedy/${remedy.id}`);
    } else {
      navigate("/sign-in");
    }
  };

  return (
    <main>
      <Hero />
      <RemediesGrid remedies={filteredRemedies} onCardClick={handleRemedyClick} />
    </main>
  );
}
