import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function RemedyDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [remedy, setRemedy] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("/remedy.json")
      .then((res) => res.json())
      .then((data) => {
        const found = data.find((r) => String(r.id) === id);
        setRemedy(found);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error loading remedy data:", err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p style={{ padding: "2rem" }}>Loading...</p>;
  if (!remedy) return <p style={{ padding: "2rem", color: "red" }}>Remedy not found.</p>;

  return (
    <section
      aria-label={`Details about ${remedy.name}`}
      style={containerStyle}
    >
      <button onClick={() => navigate(-1)} style={backButtonStyle}>
        &larr; Back
      </button>

      <h2 style={titleStyle}>{remedy.name}</h2>

      <img
        src={remedy.imageUrl}
        alt={`Image of ${remedy.name}`}
        style={imageStyle}
      />

      <div style={detailsStyle}>
        <Section title="Description" content={remedy.description} />
        <Section title="Ingredients" list={remedy.ingredients} isList />
        <Section title="Preparation Method" list={remedy.preparationMethod} isList ordered />
        <Section title="Dosage" content={remedy.dosage} />
        <Section title="Benefits" content={remedy.benefits} />
        <Section title="Precautions" content={remedy.precautions} />
      </div>
    </section>
  );
}

// Section Component
function Section({ title, content, list, isList, ordered }) {
  return (
    <section>
      <h3 style={sectionTitle}>{title}</h3>
      {isList && Array.isArray(list) ? (
        ordered ? (
          <ol style={listStyle}>{list.map((item, i) => <li key={i}>{item}</li>)}</ol>
        ) : (
          <ul style={listStyle}>{list.map((item, i) => <li key={i}>{item}</li>)}</ul>
        )
      ) : (
        <p>{content || "Not available"}</p>
      )}
    </section>
  );
}

// Styles
const containerStyle = {
  backgroundColor: "#fff",
  padding: "2rem",
  borderRadius: "1rem",
  boxShadow: "0 6px 24px rgba(0,0,0,0.1)",
  maxWidth: "960px",
  margin: "2rem auto",
};

const backButtonStyle = {
  background: "#4b2be3",
  color: "#fff",
  padding: "0.5rem 1rem",
  border: "none",
  borderRadius: "8px",
  cursor: "pointer",
  marginBottom: "1.5rem",
  fontWeight: "600",
};

const titleStyle = {
  fontSize: "2rem",
  fontWeight: "bold",
  color: "#4b2be3",
  marginBottom: "1rem",
};

const imageStyle = {
  width: "100%",
  height: "300px",
  objectFit: "cover",
  borderRadius: "12px",
  marginBottom: "2rem",
};

const detailsStyle = {
  color: "#444",
  lineHeight: "1.6",
  fontSize: "1rem",
  display: "flex",
  flexDirection: "column",
  gap: "1.5rem",
};

const sectionTitle = {
  fontSize: "1.2rem",
  fontWeight: "600",
  marginBottom: "0.5rem",
  color: "#222",
};

const listStyle = {
  paddingLeft: "1.25rem",
  margin: 0,
};
