import React, { useState, useEffect, useRef } from "react";

const Profile = ({ currentUser,setCurrentUser }) => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    avatar: "",
  });

  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const fileRef = useRef(null);
  const [file, setFile] = useState(undefined);

  // Optional states for file upload status
  const [filePerc, setFilePerc] = useState(0);
  const [fileUploadError, setFileUploadError] = useState(false);

  useEffect(() => {
    if (currentUser) {
      setFormData({
        username: currentUser.username || "",
        email: currentUser.email || "",
        password: "",
        avatar: currentUser.avatar || "",
      });
    }
  }, [currentUser]);

  useEffect(() => {
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prev) => ({ ...prev, avatar: reader.result }));
        setFileUploadError(false);
        setFilePerc(100); // Simulated success
      };
      reader.onerror = () => {
        setFileUploadError(true);
        setFilePerc(0);
      };
      reader.readAsDataURL(file);
    }
  }, [file]);

  if (!currentUser) {
    return <p className="text-center mt-5">No user data available</p>;
  }

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.id]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
  e.preventDefault();

  setMessage(null);
  setError(null);

    if (!currentUser || !currentUser.id) {
      setError("User ID is missing. Please sign in again.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:5000/user/update/${currentUser.id}`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include", // send cookies
        body: JSON.stringify(formData),
      }
    );

    const data = await response.json();

    if (!response.ok) {
      setError(data.message || "Failed to update profile");
    } else {
      setMessage("Profile updated successfully!");
      setFormData((prev) => ({ ...prev, password: "" }));

      if (typeof setCurrentUser === "function") {
        setCurrentUser(data.user);
      }
    }
  } catch (err) {
    console.error("Error updating profile:", err);
    setError("An unexpected error occurred.");
  }
};


  return (
    <div style={{padding: '3rem', maxWidth: '1000px', margin: 'auto', fontFamily: 'Georgia, serif', lineHeight: '1.8'}}>
      <h1 style={{ fontSize: '3rem', marginBottom: '1.5rem'}}>Profile</h1>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input
          type="file"
          accept="image/*"
          ref={fileRef}
          onChange={(e) => setFile(e.target.files[0])}
          hidden
        />

        <p className="text-sm self-center">
          {fileUploadError ? (
            <span className="text-red-700">
              Error uploading image (must be less than 5MB)
            </span>
          ) : filePerc > 0 && filePerc < 100 ? (
            <span className="text-slate-700">{`Uploading ${filePerc}%`}</span>
          ) : filePerc === 100 ? (
            <span className="text-green-700">Image uploaded successfully!</span>
          ) : null}
        </p>

        <img
          onClick={() => fileRef.current.click()}
          src={formData.avatar || "/assets/avatar.png"}
          alt="profile"
          className="rounded-full h-24 w-24 object-cover cursor-pointer self-center mt-2"
        />

        <input
          type="text"
          id="username"
          value={formData.username}
          onChange={handleChange}
          placeholder="Username"
          className="border p-3 rounded-lg"
          required
        />

        <input
          type="email"
          id="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          className="border p-3 rounded-lg"
          required
        />

        <input
          type="password"
          id="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          className="border p-3 rounded-lg"
        />

        <button
          type="submit"
          className="bg-slate-700 text-white p-3 rounded-lg uppercase hover:opacity-95"
        >
          Submit
        </button>
      </form>

      {message && <p className="text-green-600 text-center mt-3">{message}</p>}
      {error && <p className="text-red-600 text-center mt-3">{error}</p>}

      <div className="flex justify-between mt-5">
        <span className="text-red-700 cursor-pointer">Delete Account</span>
        <span className="text-red-700 cursor-pointer">Sign Out</span>
      </div>
    </div>
  );
};

export default Profile;
