import { GoogleAuthProvider, getAuth, signInWithPopup } from "firebase/auth";
import { app } from "../firebase.js";
import { useState } from "react";
import { useNavigate } from "react-router-dom";  // Import useNavigate

const OAuth = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();  // Initialize navigate

  const handleGoogleClick = async () => {
    setLoading(true);
    setError(null);
    try {
      const provider = new GoogleAuthProvider();
      const auth = getAuth(app);

      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      // Force refresh token to ensure it's valid and not expired
      const idToken = await user.getIdToken(true);

      const res = await fetch("http://localhost:5000/auth/google", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ token: idToken }),
      });

      if (!res.ok) {
        let errorMessage = `Unexpected response: ${res.status}`;
        try {
          const errorData = await res.json();
          errorMessage = errorData.message || errorMessage;
        } catch {
          const text = await res.text();
          errorMessage = text || errorMessage;
        }
        throw new Error(errorMessage);
      }

      const data = await res.json();
      console.log("User authenticated:", data);

      // **Redirect user to home page after successful sign in**
      navigate("/home");  

    } catch (error) {
      console.error("Could not sign in with Google", error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <button
        onClick={handleGoogleClick}
        type="button"
        disabled={loading}
        className="bg-red-700 text-white p-3 rounded-lg uppercase hover:opacity-95 disabled:opacity-60"
      >
        {loading ? "Signing in..." : "Continue with Google"}
      </button>
      {error && (
        <p className="text-red-600 mt-2 text-sm text-center">{error}</p>
      )}
    </>
  );
};

export default OAuth;
