import React from "react";

export default function RemedyCard({ remedy, onSelect }) {
  const handleKey = (e) => {
    if (e.key === "Enter") onSelect(remedy);
  };

  return (
    <article
      role="button"
      tabIndex="0"
      onClick={() => onSelect(remedy)}
      onKeyDown={handleKey}
      aria-label={`View details for ${remedy.name}`}
      style={{
        backgroundColor: "#fff",
        borderRadius: "1rem",
        boxShadow: "0 6px 16px rgba(0,0,0,0.1)",
        cursor: "pointer",
        padding: "1rem",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        transition: "transform 0.2s ease, box-shadow 0.2s ease",
      }}
      onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.02)")}
      onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1.0)")}
    >
      <img
        src={remedy.imageUrl}
        alt={remedy.name}
        loading="lazy"
        style={{
          width: "100%",
          height: "180px",
          objectFit: "cover",
          borderRadius: "0.75rem",
          marginBottom: "1rem",
        }}
      />
      <h3
        style={{
          fontSize: "1.2rem",
          fontWeight: "bold",
          color: "#4b2be3",
          marginBottom: "0.5rem",
        }}
      >
        {remedy.name}
      </h3>
      <p style={{ fontSize: "0.95rem", color: "#555" }}>{remedy.description}</p>
    </article>
  );
}
