import React from "react";

export default function RemedyDetail({ remedy, onBack }) {
  if (!remedy) return null;

  return (
    <section
      aria-label={`Details about ${remedy.name}`}
      style={{
        backgroundColor: "#fff",
        padding: "2rem",
        borderRadius: "1rem",
        boxShadow: "0 6px 24px rgba(0,0,0,0.1)",
        maxWidth: "960px",
        margin: "2rem auto",
      }}
    >
      <button
        onClick={onBack}
        style={{
          background: "#4b2be3",
          color: "#fff",
          padding: "0.5rem 1rem",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "1.5rem",
          fontWeight: "600",
        }}
      >
        &larr; Back
      </button>

      <h2 style={{ fontSize: "2rem", fontWeight: "bold", color: "#4b2be3", marginBottom: "1rem" }}>
        {remedy.name}
      </h2>

      <img
        src={remedy.imageUrl}
        alt={`Image of ${remedy.name}`}
        style={{
          width: "100%",
          height: "300px",
          objectFit: "cover",
          borderRadius: "12px",
          marginBottom: "2rem",
        }}
      />

      <div style={{ color: "#444", lineHeight: "1.6", fontSize: "1rem", display: "flex", flexDirection: "column", gap: "1.5rem" }}>
        <section>
          <h3 style={sectionTitle}>Description</h3>
          <p>{remedy.description}</p>
        </section>

        <section>
          <h3 style={sectionTitle}>Ingredients</h3>
          <ul style={listStyle}>
            {remedy.ingredients?.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </section>

        <section>
          <h3 style={sectionTitle}>Preparation Method</h3>
          <ol style={listStyle}>
            {remedy.preparationMethod?.map((step, idx) => (
              <li key={idx}>{step}</li>
            ))}
          </ol>
        </section>

        <section>
          <h3 style={sectionTitle}>Dosage</h3>
          <p>{remedy.dosage}</p>
        </section>

        <section>
          <h3 style={sectionTitle}>Benefits</h3>
          <p>{remedy.benefits}</p>
        </section>

        <section>
          <h3 style={sectionTitle}>Precautions</h3>
          <p>{remedy.precautions}</p>
        </section>
      </div>
    </section>
  );
}

// Styling helpers
const sectionTitle = {
  fontSize: "1.2rem",
  fontWeight: "600",
  marginBottom: "0.5rem",
  color: "#222",
};

const listStyle = {
  paddingLeft: "1.25rem",
  margin: 0,
};
