import React from 'react';
import { FaSearch } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';

const Header = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();

  const handleSignout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setCurrentUser(null);
    navigate('/');
  };

  return (
    <header className="bg-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-4 py-3">
        
        {/* Logo */}
        <Link to="/" className="flex items-center gap-1">
          <h1 className="text-xl sm:text-2xl font-bold text-slate-800">
            <span className="text-slate-500">Panch</span>
            <span className="text-slate-700">karma</span>
          </h1>
        </Link>

        {/* Search */}
        <form className="hidden sm:flex items-center bg-gray-100 rounded-full px-3 py-1 w-60 focus-within:ring-2 ring-blue-500">
          <input
            type="text"
            placeholder="Search..."
            className="bg-transparent outline-none w-full px-2 text-sm text-slate-700"
          />
          <FaSearch className="text-slate-500" />
        </form>

        {/* Navigation / Auth */}
        <ul className="flex items-center gap-5 text-sm sm:text-base text-slate-700 font-medium">
          <li>
            <Link to="/" className="hover:text-blue-600 transition">Home</Link>
          </li>
          <li>
            <Link to="/about" className="hover:text-blue-600 transition">About</Link>
          </li>
          <li>
            {currentUser ? (
              <div className="flex items-center gap-3">
                <Link to="/profile" className="flex items-center gap-2">
                  <img
                    src={currentUser.avatar || '/assets/avatar.png'}
                    alt="avatar"
                    className="w-9 h-9 rounded-full border-2 border-slate-300 shadow-sm object-cover"
                  />
                </Link>
                <button
                  onClick={handleSignout}
                  className="text-red-600 hover:underline text-xs sm:text-sm"
                >
                  Sign Out
                </button>
              </div>
            ) : (
              <Link
                to="/sign-in"
                className="hover:text-blue-600 transition"
              >
                Sign In
              </Link>
            )}
          </li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
