import React, { useState, useEffect } from "react";
import "../styles/ScrollToTopButton.css";

export default function ScrollToTopButton() {
  const [showButton, setShowButton] = useState(false);
  const [scrollPercent, setScrollPercent] = useState(0);

  useEffect(() => {
    function handleScroll() {
      const scrollTop = window.scrollY || document.documentElement.scrollTop;
      const docHeight =
        document.documentElement.scrollHeight - document.documentElement.clientHeight;
      const scrolled = docHeight > 0 ? (scrollTop / docHeight) * 100 : 0;

      setShowButton(scrollTop > 200);
      setScrollPercent(Math.round(scrolled));
    }

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleClick = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return showButton ? (
    <button className="scroll-top-btn" onClick={handleClick} aria-label="Scroll to top">
      {scrollPercent === 100 ? (
        <>
          <span className="arrow">&#8679;</span>
          <span className="percent">{scrollPercent}%</span>
        </>
      ) : (
        <span className="percent">{scrollPercent}%</span>
      )}
    </button>
  ) : null;
}
