import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { signInStart, signInSuccess, signInFailure } from '../redux/user/userSlice';



const SignIn = () => {
  const dispatch = useDispatch();
  const {loading,error} = useSelector((state)=>state.user);
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [setError] = useState(null);
  const [setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      dispatch(signInStart());
      const res = await fetch('http://localhost:3000/auth/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await res.json();
      console.log(data);

      if (!res.ok) {
        dispatch(signInFailure(data.message));
      } else {
        dispatch(signInSuccess(data.user));
        navigate('/');
      }
    } catch (err) {
      setError('Server error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h1 className="text-3xl text-center font-bold my-6">Sign In</h1>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input type="email" id="email" placeholder="Email" className="border p-3 rounded-lg" onChange={handleChange} required />
        <input type="password" id="password" placeholder="Password" className="border p-3 rounded-lg" onChange={handleChange} required />
        <button type="submit" className="bg-blue-600 text-white p-3 rounded-lg uppercase hover:opacity-90 disabled:opacity-60" disabled={loading}>
          {loading ? 'Signing in...' : 'Sign In'}
        </button>
      </form>
      <div className="flex gap-2 mt-4 text-sm justify-center">
        <p>Don't have an account?</p>
        <Link to="/sign-up" className="text-blue-600 hover:underline">Sign Up</Link>
      </div>
      {error && <p className="text-red-600 text-center mt-4">{error}</p>}
    </div>
  );
};

export default SignIn;
