import React from "react";

export default function RemedyCard({ remedy, onSelect }) {
  const handleKey = (e) => {
    if (e.key === "Enter" || e.key === " ") {
      onSelect(remedy);
    }
  };

  return (
    <article
      className="remedy-card"
      role="button"
      tabIndex="0"
      aria-label={`View details for ${remedy.name}`}
      aria-labelledby={`remedy-title-${remedy.id}`}
      aria-describedby={`remedy-desc-${remedy.id}`}
      onClick={() => onSelect(remedy)}
      onKeyDown={handleKey}
    >
      <img
        src={remedy.imageUrl}
        alt={remedy.name || "Remedy image"}
        loading="lazy"
        className="remedy-card-image"
      />
      <h3
        className="remedy-card-title"
        id={`remedy-title-${remedy.id}`}
      >
        {remedy.name}
      </h3>
      <p
        className="remedy-card-desc"
        id={`remedy-desc-${remedy.id}`}
      >
        {remedy.description}
      </p>
    </article>
  );
}
