import React, { useState, useEffect } from "react";
import RemedyCard from "./RemedyCard";

// Hero section component
export function Hero() {
  return (
    <section
      style={{ position: "relative", textAlign: "center", marginBottom: 40 }}
      aria-label="Homepage Hero Section"
    >
      <img
        src="https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/3626d480-e17b-42d3-851b-d23b1d889bc3.png"
        alt="Natural Ayurvedic herbs and remedies display"
        style={{
          width: "100%",
          maxHeight: 400,
          objectFit: "cover",
          borderRadius: 12,
          filter: "brightness(0.75)",
        }}
      />
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "white",
          maxWidth: "90%",
          textShadow: "0 0 12px rgba(0,0,0,0.7)",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", fontWeight: 700, marginBottom: 12 }}>
          Welcome to Ayurveda Healing
        </h1>
        <p style={{ fontSize: "1.25rem", fontWeight: 300 }}>
          Discover natural and home-based Ayurvedic remedies for holistic health
          and wellness.
        </p>
      </div>
    </section>
  );
}

function RemediesGrid({ remedies, onSelectRemedy }) {
  if (!remedies.length) {
    return <p style={{ padding: "1rem" }}>No remedies found for selected filter.</p>;
  }
  return (
    <section
      aria-label="List of Ayurvedic remedies"
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
        gap: 20,
        marginBottom: 40,
      }}
    >
      {remedies.map((remedy) => (
        <RemedyCard key={remedy.id} remedy={remedy} onSelect={onSelectRemedy} />
      ))}
    </section>
  );
}

// Main Home Component
export default function Home({ onSelectRemedy, searchQuery = "" }) {
  const [remediesData, setRemediesData] = useState([]);
  const [filterCategory, setFilterCategory] = useState("");
  const [filteredRemedies, setFilteredRemedies] = useState([]);

  useEffect(() => {
    fetch('/remedy.json')
      .then((response) => response.json())
      .then((data) => {
        setRemediesData(data);
        setFilteredRemedies(data);
      });
  }, []);

  useEffect(() => {
    let result = remediesData;

    if (filterCategory) {
      result = result.filter((rem) => rem.category === filterCategory);
    }

    if (searchQuery.trim()) {
      result = result.filter((rem) =>
        rem.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFilteredRemedies(result);
  }, [filterCategory, searchQuery, remediesData]);

  const categories = Array.from(new Set(remediesData.map((r) => r.category).filter(Boolean)));

  return (
    <main>
      <Hero />
      <Filters
        categories={categories}
        currentCategory={filterCategory}
        onFilterChange={setFilterCategory}
      />
      <RemediesGrid remedies={filteredRemedies} onSelectRemedy={onSelectRemedy} />
    </main>
  );
}
