import React from 'react';

const Chatbot = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <div
      style={{
        position: 'fixed',
        left: '20px',
        bottom: '90px',
        width: '300px',
        height: '400px',
        backgroundColor: 'white',
        boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
        borderRadius: '8px',
        zIndex: 1000,
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      <div
        style={{
          padding: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          fontWeight: 'bold',
          borderTopLeftRadius: '8px',
          borderTopRightRadius: '8px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <span>Chatbot</span>
        <button
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            color: 'white',
            fontSize: '20px',
            cursor: 'pointer',
          }}
          aria-label="Close Chatbot"
          title="Close Chatbot"
        >
          &times;
        </button>
      </div>
      <div
        style={{
          flex: 1,
          padding: '10px',
          overflowY: 'auto',
          fontSize: '14px',
          color: '#333',
        }}
      >
        {/* Chatbot content goes here */}
        <p>Hello! How can I help you today?</p>
      </div>
      <div
        style={{
          padding: '10px',
          borderTop: '1px solid #ddd',
        }}
      >
        <input
          type="text"
          placeholder="Type your message..."
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '4px',
            border: '1px solid #ccc',
          }}
          disabled
        />
      </div>
    </div>
  );
};

export default Chatbot;
