import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import OAuth from '../components/OAuth';
import { useAuth } from '../context/AuthContext';

const SignIn = ({ setCurrentUser }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { signin } = useAuth();

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const res = await fetch('http://localhost:5000/auth/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message || 'Login failed');
      } else {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setCurrentUser(data.user);
        await signin(formData.email, formData.password);
        navigate('/');
      }
    } catch (err) {
      setError('Server error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-8 bg-white rounded-lg shadow-md">
      <h1 className="text-4xl font-bold text-center text-slate-800 mb-6">
        Welcome Back
      </h1>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input
          type="email"
          id="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
          required
        />
        <input
          type="password"
          id="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          className="border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-slate-500"
          required
        />
        <button
          type="submit"
          className="bg-slate-700 text-white p-3 rounded-md font-medium hover:bg-slate-700 transition disabled:opacity-60"
          disabled={loading}
        >
          {loading ? 'Signing in...' : 'Sign In'}
        </button>
      </form>

      <div className="flex items-center gap-2 my-5">
        <hr className="flex-grow border-gray-300" />
        <span className="text-gray-500 text-sm">OR</span>
        <hr className="flex-grow border-gray-300" />
      </div>

      <OAuth setCurrentUser={setCurrentUser} />

      <div className="text-center mt-4 text-sm text-gray-600">
        Donâ€™t have an account?{' '}
        <Link to="/sign-up" className="text-blue-600 hover:underline">
          Sign Up
        </Link>
      </div>

      {error && (
        <p className="text-red-600 text-center mt-4 font-medium">{error}</p>
      )}
    </div>
  );
};

export default SignIn;
