import React from "react";

export default function RemedyCard({ remedy, onSelect }) {
  return (
    <article
      tabIndex="0"
      role="button"
      onClick={() => onSelect(remedy)}
      onKeyPress={(e) => {
        if (e.key === "Enter") onSelect(remedy);
      }}
      style={{
        backgroundColor: "white",
        borderRadius: 16,
        boxShadow: "0 6px 20px rgba(0,0,0,0.1)",
        cursor: "pointer",
        padding: 16,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        transition: "transform 0.2s ease",
      }}
      aria-label={`View details for ${remedy.name}`}
    >
      <img
        src={remedy.imageUrl}
        alt={`Image of ${remedy.name}`}
        style={{ width: "100%", height: 180, objectFit: "cover", borderRadius: 12, marginBottom: 12 }}
        loading="lazy"
      />
      <h3 style={{ fontWeight: 700, fontSize: "1.25rem", marginBottom: 8, color: "#4b2be3" }}>{remedy.name}</h3>
      <p style={{ fontSize: "0.95rem", color: "#555" }}>{remedy.description}</p>
    </article>
  );
}
