import React, { useState, useEffect } from "react";
import RemedyCard from "./RemedyCard";
import remediesData from "./remedy.json";

export function Hero() {
  return (
    <section
      style={{ position: "relative", textAlign: "center", marginBottom: 40 }}
      aria-label="Homepage Hero Section"
    >
      <img
        src="https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/3626d480-e17b-42d3-851b-d23b1d889bc3.png"
        alt="Natural Ayurvedic herbs and remedies display"
        style={{
          width: "100%",
          maxHeight: 400,
          objectFit: "cover",
          borderRadius: 12,
          filter: "brightness(0.75)",
        }}
      />
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "white",
          maxWidth: "90%",
          textShadow: "0 0 12px rgba(0,0,0,0.7)",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", fontWeight: 700, marginBottom: 12 }}>
          Welcome to Ayurveda Healing
        </h1>
        <p style={{ fontSize: "1.25rem", fontWeight: 300 }}>
          Discover natural and home-based Ayurvedic remedies for holistic health
          and wellness.
        </p>
      </div>
    </section>
  );
}

function Filters({ categories, currentCategory, onFilterChange }) {
  return (
    <div
      style={{
        marginBottom: 24,
        display: "flex",
        alignItems: "center",
        gap: 12,
        justifyContent: "center",
      }}
    >
      <label
        htmlFor="categoryFilter"
        style={{ fontWeight: 600, fontSize: "1rem" }}
      >
        Filter by Category:
      </label>
      <select
        id="categoryFilter"
        value={currentCategory}
        onChange={(e) => onFilterChange(e.target.value)}
        style={{ padding: "6px 10px", borderRadius: 6, border: "1px solid #ccc", fontSize: "1rem" }}
      >
        <option value="">All</option>
        {categories.map((cat) => (
          <option key={cat} value={cat}>
            {cat}
          </option>
        ))}
      </select>
    </div>
  );
}

function RemediesGrid({ remedies, onSelectRemedy }) {
  if (!remedies.length) {
    return <p style={{ padding: "1rem" }}>No remedies found for selected filter.</p>;
  }
  return (
    <section
      aria-label="List of Ayurvedic remedies"
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
        gap: 20,
        marginBottom: 40,
      }}
    >
      {remedies.map((remedy) => (
        <RemedyCard key={remedy.id} remedy={remedy} onSelect={onSelectRemedy} />
      ))}
    </section>
  );
}

export default function Home({ onSelectRemedy }) {
  const [filterCategory, setFilterCategory] = useState("");
  const [filteredRemedies, setFilteredRemedies] = useState(remediesData);

  const categories = Array.from(
    new Set(remediesData.map((rem) => rem.category).filter(Boolean))
  );

  useEffect(() => {
    if (filterCategory === "") {
      setFilteredRemedies(remediesData);
    } else {
      setFilteredRemedies(remediesData.filter((rem) => rem.category === filterCategory));
    }
  }, [filterCategory]);

  return (
    <main>
      <Hero />
      <Filters
        categories={categories}
        currentCategory={filterCategory}
        onFilterChange={setFilterCategory}
      />
      <RemediesGrid remedies={filteredRemedies} onSelectRemedy={onSelectRemedy} />
    </main>
  );
}
